mapper.select:
select 
  
from t_sys_user
where state !='1' and state !='2' and username = #{username}
    and password=#{password}

mapper.update:
update t_sys_user
set state=#{status}
where id = #{id,jdbcType=BIGINT}

update t_sys_user
set password=#{password}
where id = #{id,jdbcType=BIGINT}
